mapper.select:
SELECT operate_permission_id AS operatePermissionId, operate_permission_name AS operatePermissionName, code AS code, creator_id AS creatorId, createtime AS createtime FROM operate_permission


    operate_permission_name=#{keywords.permsName}


    code=#{keywords.code}


    creator_id=#{keywords.creatorId}


    createtime=#{keywords.createtime}

select op.operate_permission_id as operatePermissionId,op.operate_permission_name as operatePermissionName, op.code as code from operate_permission op where 1=1
and op.operate_permission_name LIKE CONCAT('%',CONCAT(#{operatePermissionName},'%'))
and op.operate_permission_id in (select operate_permission_id from menu_operate_permission where 1=1
and menu_id = #{menuId}
)
and op.operate_permission_id not in (select operate_permission_id from menu_operate_permission where 1=1
and menu_id = #{menuId}
)
ORDER BY op.createtime desc
