mapper.update:
		UPDATE sys_user_info
		SET
		lastUpdate=NOW()

			,username=#{username}


			,createtime=#{createtime}


			,employeeNo=#{employeeNo}


			,loginPwd=#{loginPwd}


			,remark=#{remark}


			,enabled=#{enabled}


			,nickname=#{nickname}


			,email=#{email}


			,is_member=#{isMember}

				,member_expirationdate = IF(member_expirationdate IS
				NULL, DATE_ADD(NOW(), INTERVAL 1
				YEAR),
				DATE_ADD(IF(member_expirationdate >= NOW(),
				member_expirationdate,
				NOW()), INTERVAL 1 YEAR ))



			,`birthday`=#{birthday}


			,`sex`=#{sex}


            ,`key`=#{key}


			,`type`=#{type}


            ,`money`=#{money}


			,`regist`=#{regist}


			,`vipEndTime`=#{vipEndTime}


			,status=#{status}


			,flag=#{flag}

		WHERE
		userId=#{userId}

		UPDATE sys_user_info


				username=#{username},


				createtime=#{createtime},


				employeeNo=#{employeeNo},


				loginPwd=#{loginPwd},


				remark=#{remark},


				enabled=#{enabled},


				lastUpdate=#{lastUpdate},


				nickname=#{nickname},


				email=#{email},


				userCompany=#{userCompany},


				userPosition=#{userPosition},


				industry=#{industry},


				is_member=#{isMember},

					member_expirationdate = IF(member_expirationdate IS
					NULL, DATE_ADD(NOW(), INTERVAL 1
					YEAR ),
					DATE_ADD(IF(member_expirationdate >= NOW(),
					member_expirationdate,
					NOW()), INTERVAL 1 YEAR )),




			,`birthday`=#{birthday}


			,`sex`=#{sex}


            ,`key`=#{key}


            ,`money`=#{money}


			,`regist`=#{regist}


			,`vipEndTime`=#{vipEndTime}


			,status=#{status}


			,flag=#{flag}

		WHERE
		username=#{username}

mapper.select:
select * from sys_user_info u
WHERE 1=1

	AND u.employeeNo=#{employeeNo}


	AND u.username=#{username}


	AND u.userId=#{userId}


	AND u.industry=#{industry}


	AND u.isMember=#{isMember	}


	AND u.type=#{type}


	limit ${startCount},${endCount}

select count(1) from sys_user_info u
WHERE 1=1

	AND u.employeeNo=#{employeeNo}


	AND u.username=#{username}


	AND u.userId=#{userId}


	AND u.regist=#{regist}


	limit ${startCount},${endCount}

select u.* from sys_user_info u
WHERE 1=1

	AND u.userId=#{id}

select userId, employeeNo, enabled as status, username AS username, remark AS trueName from sys_user_info WHERE 1=1 AND type=1
AND (remark like concat('%',#{username},'%') or employeeNo like concat('%',#{username},'%') or username like concat('%',#{username},'%'))
AND enabled=#{status}
limit #{startCount},#{endCount}

SELECT userId, username, username phoneNo, nickname, employeeNo, email, userPosition, userCompany, industry from sys_user_info where 1=1 and type = 0
AND username like #{keyWord}
limit #{startCount},#{endCount}

SELECT REPLACE(group_concat(eo.deptName),',','-') deptName FROM erp_organization eo WHERE 1=1 AND
eo.id=#{item}
order by eo.deptSort asc
